import {FaTimes} from 'react-icons/fa'

const Task = ({task,onClick,setTask,rawData,onDoubleClick}) => {
  if(task.reminder){
  return (
    <div className="task border-green" id={task.id} onDoubleClick={()=>{onDoubleClick(task,setTask)}}>
        <h3>{task.text}
        <FaTimes
          style={{color:'red',cursor:'pointer'}}
          onClick={()=>onClick(rawData,setTask,task.id)}
        />
        </h3>
        <p>{task.day}</p>
        
    </div>
  )}
  else{
    return (
      <div className="task" id={task.id} onDoubleClick={()=>{onDoubleClick(task,setTask)}}>
          <h3>{task.text}
          <FaTimes
            style={{color:'red',cursor:'pointer'}}
            onClick={()=>onClick(rawData,setTask,task.id)}
          />
          </h3>
          <p>{task.day}</p>
          
      </div>
    )
  }
}

export default Task
