import { FaTimes } from 'react-icons/fa';
import Button from './Button';
const AddTask = ({onSubmit,Close,parentId}) => {

  const id="AddTask";
  
  return (
    <form action="" id="AddTask" className="form-control " >

        <div style={{display:"flex",marginBottom:"20px"}}>
          <h2>Add Task</h2>

          <FaTimes style={{fontSize:"28px",marginLeft:"auto",cursor:"pointer"}} onClick={()=>Close(id,parentId)}/>

        </div>

        <label htmlFor="task">Task</label>
       
        <input type="text" name="task" id="task" />
       
        <label htmlFor="date">Date</label>
       
        <input type="text" name="date" id="date" />
       
       <div className="form-control-check">
          <label htmlFor="reminder">Set Reminder</label>

          <input type="checkbox" name="reminder" id="reminder" />
       </div>
       
       <Button class="submit" text="Submit" />
        <script>
          {
            document.querySelector('button').addEventListener('click',(e)=>{
              e.preventDefault();
            })
          }
        </script>
    </form>
  )
}

export default AddTask
