import React from 'react';
import Header from './component/Header';
import Tasks from './component/Tasks';
import { useState } from 'react';//REACT HOOKS API
import AddTask from './component/AddTask';
import * as Event from './component/event'

const App = ()=> {
  const[tasks,setTask] = useState([
    {
        "id": 1,
        "text": "Wake Up",
        "day": "Feb 3th 2019 at 06:30",
        "reminder" : true
    },
    {
        "id": 2,
        "text": "Breakfast",
        "day": "Feb 3th 2019 at 06:55",
        "reminder" : false
    },
    {
        "id": 3,
        "text": "Workup",
        "day": "Feb 3th 2019 at 07:30",
        "reminder" : true
    }
]);

  return (
    // PARENT ELEMENT CAN LIKE THIS
    <div className="container">
      <Header onClick={Event.ToggleShow}/>
      <AddTask style={{display:"none"}} onSubmit={Event.Submit}/>
      <div className="add-form">
        <Tasks data={tasks} onDelete={Event.Close}/>
      </div>



    </div>

    // OR LIKE THIS
    // <>
